			  }
			}
			// return resp.json();
		  })
		  .then(actList => this.setState({
			actList
		  }));
		}

		loadLatLng(){
    console.log("About to call:", APIlatlng)
    fetch(APIlatlng)
      .then(res => res.json())
      .then(result => this.setState({locations:result}))
      .catch(err => console.log('❌❌❌', err))
  }

	  componentWillMount() {
		this.loadActors();
	}


	render(){
		console.log('frommmmm actors component file')
