import React, { Component} from 'react';
import { Map, GoogleAPI, GoogleApiWrapper, InfoWindow, Marker} from 'google-maps-react';
import CurrentLoc from './CurrentLoc';
import MarkerCluster from './MarkerCluster';
import {Container, Row, Col }from 'react-bootstrap'
// import MarkerClusterer from '../public/markerclusterer/markerclusterer.js'
// import {default as MarkerClusterer} from "react-google-maps/lib/components/addons/MarkerClusterer";
// import MarkerClusterer from 'node-js-marker-clusterer';
// import { MarkerClusterer } from '@react-google-maps/api';
import MarkerClusterer from '/Users/tpl9/Dev/WWTF/client/src/markerclusterer/markerclusterer.js'

const google=window.google

export class MapContainer extends Component {
    state = {
        showingInfoWindow: false,
        activeMarker: {},
        selectedPlace: {},
        // markerCluster: {}

    };

    onMarkerClick = (props, marker, e) =>
        this.setState({
            selectedPlace: props,
            activeMarker: marker,
            showingInfoWindow: true
        });

    onClose = props => {
        if (this.state.showingInfoWindow) {
        this.setState({
            showingInfoWindow: false,
            activeMarker: null
            });
        }
    };



    render() {
        // console.log(this.props.locations)
        // console.log('😬',MarkerClusterer)
        // var markerCluster = new MarkerClusterer(this.props.maps, this.props.locations,
        //     {imagePath: '/markerclusterer/m'});
        //     console.log('😬😬😬',markerCluster)

        var markers = []
        if (this.props.locations.length > 0) {
            markers = this.props.locations.map((latlngval, idx) => {
                return <Marker
                        key={idx}
                        position={latlngval}
                        onClick={this.onMarkerClick}
                        name={this.props.locations[idx].title}/>
            })
        }
        console.log("!!!",GoogleAPI)
        return (

            <Container id="mapComponent">

                    <Map google={GoogleAPI}>

                        <MarkerCluster
                            markers={markers}
                            click={this.onMarkerClick}
                            mouseover={this.onMouseOver}
                            mouseout={this.onMouseOut}
                        />

                        <Marker
                            onClick={this.onMarkerClick} name={'YOU ARE HERE📍'}
                        />


                        <InfoWindow
                            marker={this.state.activeMarker}
                            visible={this.state.showingInfoWindow}
                            onClose={this.onClose}
                        >
                            <div>
                                <h4>{this.state.selectedPlace.name}</h4>
                            </div>
                        </InfoWindow>
                    </Map>

            </Container>
        )
    }
}


export default GoogleApiWrapper({
    apiKey: 'AIzaSyBANS3n7z4t5krZlgs8Kq7PuYINovATF2s'
  })(MapContainer);
