import React, { Component} from 'react';
import { GoogleApiWrapper, InfoWindow, Marker} from 'google-maps-react';
import CurrentLoc from './CurrentLoc';
import {Container, Row, Col }from 'react-bootstrap'
// import MarkerClusterer from '../public/markerclusterer/markerclusterer.js'
// import {default as MarkerClusterer} from "react-google-maps/lib/components/addons/MarkerClusterer";
// import MarkerClusterer from 'node-js-marker-clusterer';
import { MarkerClusterer } from '@react-google-maps/api';






export class MapContainer extends Component {
    state = {
        showingInfoWindow: false,
        activeMarker: {},
        selectedPlace: {},
        // markerCluster: {}

    };

    onMarkerClick = (props, marker, e) =>
        this.setState({
            selectedPlace: props,
            activeMarker: marker,
            showingInfoWindow: true
        });

    onClose = props => {
        if (this.state.showingInfoWindow) {
        this.setState({
            showingInfoWindow: false,
            activeMarker: null
            });
        }
    };



    // {this.props.locations.map((latlngval, idx) => {
    //     return <Marker
    //             key={idx}
    //             position={latlngval}
    //             onClick={this.onMarkerClick}
    //             name={this.props.locations[idx].title}/>
    // })}

    render() {
        console.log(this.props.locations)
        let iconMarker = new window.google.maps.MarkerImage(
            "./Images‚Å©/cinema.png",
            null,
            null,
            null,
            new window.google.maps.Size(32,32)
            )

        return (

            <Container>
                <Row>
                    <Col xs={6}></Col>
                        <CurrentLoc
                            centerAroundCurrentLocation
                            google={this.props.google}
                            map={this.props.map}

                        >
                        <Marker
                        onClick={this.onMarkerClick} name={'YOU ARE HEREüìç'} icon={iconMarker} />
                        {this.props.locations.map((latlngval, idx) => {
                            return <Marker
                                key={idx}
                                position={latlngval}
                                onClick={this.onMarkerClick}
                                title={this.props.locations[idx].title}
                                />
                        })}

                        <InfoWindow
                            marker={this.state.activeMarker}
                            visible={this.state.showingInfoWindow}
                            onClose={this.onClose}
                        >
                            <div>
                                <h4>{this.state.selectedPlace.name}</h4>
                            </div>
                        </InfoWindow>
                        </CurrentLoc>
                </Row>
            </Container>
        )
    }
}


export default GoogleApiWrapper({
    apiKey: 'AIzaSyC6iwluhQwZnoy6wq9LejqcbLrwRQ5khgI'
  })(MapContainer);
