import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { Collapse, Navbar, NavbarToggler, NavbarBrand, Nav, NavItem } from 'reactstrap';
import { NavLink} from 'react-router-dom'
import SearchBox from './Search';
// import Main from './main'


class Menu extends React.Component {
  constructor(props) {
    super(props);

    this.toggleNavbar = this.toggleNavbar.bind(this);
    this.state = {
      collapsed: true
    };
  }

  toggleNavbar() {
    this.setState({
      collapsed: !this.state.collapsed
    });
  }
  render() {

    let mainComponent = ""
    switch(this.props.location){
      case "":
        mainComponent = <Main {...this.props}/>
        break;
      case "callback":
        mainComponent = <Callback />
        break;
      case "secret":
        mainComponent = this.props.auth.isAuthenticated() ? <Secret {...this.props}/> : <NotFound />
        break;
      default:
        mainComponent = <NotFound />
    }
    return (

        <div>
          <Navbar color="faded" light>
            <NavbarBrand href="/" className="dropdown">Where Was That Filmed?</NavbarBrand>
            <NavbarToggler onClick={this.toggleNavbar} className="mr-2" />
            <Collapse isOpen={!this.state.collapsed} navbar>
            <SearchBox />
              <Nav navbar>
                <NavItem>
                  <NavLink to='/AllTitles'>Titles</NavLink>
                </NavItem>
                <NavItem>
                    <NavLink to='/AllActors'>Actors</NavLink>
                </NavItem>

                {mainComponent}
              </Nav>
              </Collapse>
          </Navbar>
        </div>



    );
  }
}

export default Menu;
