export class MapContainer extends Component {
    state = {
        showingInfoWindow: false,
        activeMarker: {},
        selectedPlace: {},
        locations:  [
                    {lat: 37.7698646, lng: -122.4660947},
                    {lat: 37.775144, lng: -122.4039924},
                    {lat: 37.7749295, lng: -122.4194155},
                    {lat: 37.793997, lng: -122.4023388}
                    ]
    };

    onMarkerClick = (props, marker, e) =>
        this.setState({
            selectedPlace: props,
            activeMarker: marker,
            showingInfoWindow: true
        });

    onClose = props => {
        if (this.state.showingInfoWindow) {
        this.setState({
            showingInfoWindow: false,
            activeMarker: null
            });
        }
    };

    render() {
        return (
            <div className='MapContainerDiv'>
                <CurrentLoc
                    centerAroundCurrentLocation
                    google={this.props.google}
                >
                <Marker onClick={this.onMarkerClick} name={'YOU ARE HEREüìç'} />
                {this.state.locations.map((latlngval, idx) => {
                    return <Marker key={idx} position={latlngval} onClick={this.onMarkerClick} name={'Exampleüìç'} />
                })}
                <InfoWindow
                    marker={this.state.activeMarker}
                    visible={this.state.showingInfoWindow}
                    onClose={this.onClose}
                >
                    <div>
                        <h4>{this.state.selectedPlace.name}</h4>
                    </div>
                </InfoWindow>
                </CurrentLoc>
            </div>
        )
    }
}


